refentry.refsect1:
&reftitle.description;

 mixedcurl_getinfo
 CurlHandlehandle
 intnulloption&null;



 Получает информацию о последней передаче.

  &reftitle.examples;



    Пример использования функции curl_getinfo


<?php

// Создаём дескриптор cURL
$ch = curl_init('http://www.example.com/');

// Запускаем
curl_exec($ch);

// Проверяем наличие ошибок
if (!curl_errno($ch)) {
  $info = curl_getinfo($ch);
  echo 'Прошло ', $info['total_time'], ' секунд во время запроса к ', $info['url'], "\n";
}

// Закрываем дескриптор
curl_close($ch);

?>








     Пример использования функции curl_getinfo
     с параметром option



<?php

// Создаём дескриптор cURL
$ch = curl_init('http://www.example.com/');

// Запускаем
curl_exec($ch);

// Проверяем наличие ошибок
if (!curl_errno($ch)) {
  switch ($http_code = curl_getinfo($ch, CURLINFO_HTTP_CODE)) {
    case 200:  # OK
      break;
    default:
      echo 'Неожиданный код HTTP: ', $http_code, "\n";
  }
}

// Закрываем дескриптор
curl_close($ch);

?>
