refentry.refsect1:
&reftitle.description;
booldb2_set_option resourceresource arrayoptions inttype
Функция устанавливает опции для ресурса запроса или соединения. Нельзя установить опции для ресурсов наборов результатов.

  &reftitle.examples;




    Пример установки одного параметра для ресурса соединения


<?php

/* Параметры соединения с базой данных */
$database = 'SAMPLE';
$hostname = 'localhost';
$port = 50000;
$protocol = 'TCPIP';
$username = 'db2inst1';
$password = 'ibmdb2';

/* Строка соединения */
$conn_string = "DRIVER={IBM DB2 ODBC DRIVER};DATABASE=$database;";
$conn_string .= "HOSTNAME=$hostname;PORT=$port;PROTOCOL=$protocol;";
$conn_string .= "UID=$username;PWD=$password;";

/* Получаем ресурс соединения */
$conn = db2_connect($conn_string, '', '');

/* Создаём ассоциативный массив опций */
$options = array('autocommit' => DB2_AUTOCOMMIT_ON);

/* Вызываем функцию */
$result = db2_set_option($conn, $options, 1);

/* Проверяем, все ли опции установились */
if($result) {
    echo 'Options Set Successfully';
} else {
    echo 'Could Not Set Options';
}

?>


       &example.outputs;
Options Set Successfully



      Пример установки набора параметров для ресурса соединения


<?php

/* Параметры соединения с базой данных */
$database = 'SAMPLE';
$hostname = 'localhost';
$port = 50000;
$protocol = 'TCPIP';
$username = 'db2inst1';
$password = 'ibmdb2';

/* Строка соединения */
$conn_string = "DRIVER={IBM DB2 ODBC DRIVER};DATABASE=$database;";
$conn_string .= "HOSTNAME=$hostname;PORT=$port;PROTOCOL=$protocol;";
$conn_string .= "UID=$username;PWD=$password;";

/* Получаем ресурс соединения */
$conn = db2_connect($conn_string, '', '');

/* Создаём ассоциативный массив опций */
$options = array(
    'autocommit' => DB2_AUTOCOMMIT_OFF,
    'binmode' => DB2_PASSTHRU,
    'db2_attr_case' => DB2_CASE_UPPER,
    'cursor' => DB2_SCROLLABLE
);

/* Вызываем функцию */
$result = db2_set_option($conn, $options, 1);

/* Проверяем, все ли опции установились */
if ($result) {
    echo 'Options Set Successfully';
} else {
    echo 'Could Not Set Options';
}

?>


       &example.outputs;
Options Set Successfully




