      Пример установки набора параметров с неправильным ресурсом


<?php

/* Параметры соединения с базой данных */
$database = 'SAMPLE';
$hostname = 'localhost';
$port = 50000;
$protocol = 'TCPIP';
$username = 'db2inst1';
$password = 'ibmdb2';

/* Строка соединения */
$conn_string = "DRIVER={IBM DB2 ODBC DRIVER};DATABASE=$database;";
$conn_string .= "HOSTNAME=$hostname;PORT=$port;PROTOCOL=$protocol;";
$conn_string .= "UID=$username;PWD=$password;";

/* Получаем ресурс соединения */
$conn = db2_connect($conn_string, '', '');

/* Создаём ассоциативный массив опций */
$options = array(
    'autocommit' => DB2_AUTOCOMMIT_OFF,
    'binmode' => DB2_PASSTHRU,
    'db2_attr_case' => DB2_CASE_UPPER,
    'cursor' => DB2_SCROLLABLE
);

$stmt = db2_prepare($conn, 'SELECT * FROM EMPLOYEE');

/* Вызываем функцию */
$result = db2_set_option($stmt, $options, 1);

/* Проверяем, все ли опции установились */
if ($result) {
    echo 'Options Set Successfully';
} else {
    echo 'Could Not Set Options';
}

?>


