  &reftitle.description;

&style.oop;

   public objectfalsemysqli_result::fetch_field_direct
   intindex


&style.procedural;

   objectfalsemysqli_fetch_field_direct
   mysqli_resultresult
   intindex



   Метод возвращает информацию о столбце результирующего набора в виде объекта.

&reftitle.parameters;



  &mysqli.result.description;

   index



     Номер поля. Число в диапазоне от
     0 до количество полей - 1.

&reftitle.returnvalues;


 Метод возвращает объект, который содержит определение поля, или &false;, если поле с номером,
 который задали для параметра index, недоступно.

  &reftitle.examples;

   &style.oop;


<?php

$mysqli = new mysqli("localhost", "my_user", "my_password", "world");

/* Проверяем подключение */
if (mysqli_connect_errno()) {
    printf("Не удалось подключиться: %s\n", mysqli_connect_error());
    exit();
}

$query = "SELECT Name, SurfaceArea from Country ORDER BY Name LIMIT 5";

if ($result = $mysqli->query($query)) {
    /* Получаем метаданные столбца SurfaceArea */
    $finfo = $result->fetch_field_direct(1);

        printf("Имя:         %s\n", $finfo->name);
        printf("Таблица:     %s\n", $finfo->table);
        printf("Макс. длина: %d\n", $finfo->max_length);
        printf("Флаги:       %d\n", $finfo->flags);
        printf("Тип:         %d\n\n", $finfo->type);

    $result->close();
}

/* Закрываем подключение */
$mysqli->close();

?>




   &style.procedural;


<?php

$link = mysqli_connect("localhost", "my_user", "my_password", "world");

/* Проверяем подключение */
if (mysqli_connect_errno()) {
    printf("Не удалось подключиться: %s\n", mysqli_connect_error());
    exit();
}

$query = "SELECT Name, SurfaceArea from Country ORDER BY Name LIMIT 5";

if ($result = mysqli_query($link, $query)) {

    /* Получаем метаданные столбца SurfaceArea */
    $finfo = mysqli_fetch_field_direct($result, 1);

    printf("Имя:         %s\n", $finfo->name);
    printf("Таблица:     %s\n", $finfo->table);
    printf("Макс. длина: %d\n", $finfo->max_length);
    printf("Флаги:       %d\n", $finfo->flags);
    printf("Тип:         %d\n\n", $finfo->type);

    mysqli_free_result($result);
}

/* Закрываем подключение */
mysqli_close($link);

?>


   &examples.outputs;


Имя:         SurfaceArea
Таблица:     Country
Макс. длина: 10
Флаги:       32769
Тип:         4
