   –ü—Ä–∏–º–µ—Ä –∏—Å–ø–æ–ª—å–∑–æ–≤–∞–Ω–∏—è Random\Randomizer::pickArrayKeys


<?php
$r = new \Random\Randomizer();

$fruits = [ 'red' => 'üçé', 'green' => 'ü•ù', 'yellow' => 'üçå', 'pink' => 'üçë', 'purple' => 'üçá' ];

// –í—ã–±–æ—Ä–∫–∞ 2 —Å–ª—É—á–∞–π–Ω—ã—Ö –∫–ª—é—á–µ–π –º–∞—Å—Å–∏–≤–∞:
echo "–ö–ª—é—á–∏: ", join( ', ', $r->pickArrayKeys( $fruits, 2 ) ), "\n";

// –í—ã–±–æ—Ä–∫–∞ –µ—â—ë 3:
echo "–ö–ª—é—á–∏: ", join( ', ', $r->pickArrayKeys( $fruits, 3 ) ), "\n";
?>


   &example.outputs.similar;


–ö–ª—é—á–∏: yellow, purple
–ö–ª—é—á–∏: red, green, yellow




   –í—ã–±–æ—Ä —Å–ª—É—á–∞–π–Ω—ã—Ö –∑–Ω–∞—á–µ–Ω–∏–π


<?php
$r = new \Random\Randomizer();

$fruits = [ 'red' => 'üçé', 'green' => 'ü•ù', 'yellow' => 'üçå', 'pink' => 'üçë', 'purple' => 'üçá' ];

$keys = $r->pickArrayKeys($fruits, 2);
// –ü–æ–∏—Å–∫ –∑–Ω–∞—á–µ–Ω–∏—è –¥–ª—è –≤—ã–±—Ä–∞–Ω–Ω—ã—Ö –∫–ª—é—á–µ–π.
$selection = array_map(
    static fn ($key) => $fruits[$key],
    $keys
);

echo "–ó–Ω–∞—á–µ–Ω–∏—è: ", join( ', ', $selection ), "\n";
?>


   &example.outputs.similar;


–ó–Ω–∞—á–µ–Ω–∏—è: üçé, üçá
