Пример использования stream_get_meta_data с вызовом функции stream_socket_client с HTTPS-протоколом
<?php
$streamContext = stream_context_create( [ 'ssl' => [ 'capture_peer_cert' => true, 'capture_peer_cert_chain' => true, 'disable_compression' => true, ], ] );
$client = stream_socket_client( 'ssl://www.example.com:443', $errorNumber, $errorDescription, 40, STREAM_CLIENT_CONNECT, $streamContext );
$meta = stream_get_meta_data($client);
var_dump($meta);
?>
