  &reftitle.examples;



    Пример разбора файла методом tidy::parseFile


<?php

$tidy = new tidy();
$tidy->parseFile('file.html');

$tidy->cleanRepair();

if (!empty($tidy->errorBuffer)) {
    echo "Возникли следующие ошибки или предупреждения:\n";
    echo $tidy->errorBuffer;
}

?>
